INSERT INTO `ec` (`ec_id`, `ue_id`, `groupe_id`, `ec_code`, `ec_nom`, `ec_competence`, `ec_prerequis`, `ec_contenu`, `ec_coef`, `ec_nbre_heure_cm`, `ec_nbre_heure_td`, `ec_nbre_heure_tp`, `ec_nbre_heure_tpe`) VALUES
(1, 1, NULL, 'DUTINFO 1111', 'Initiation à l\'informatique', '• Savoir installer et configurer un système informatique\n• Faire du conseil et assistance technique à des utilisateurs, clients, services\n• Comprendre un système informatique\n• Savoir utililiser les outils bureautiques', 'Aucun', '• Codage de l’information : nombres et caractères. Arithmétique et traitements associés\n• Architecture générale d\'un système informatique\n• Types et caractéristiques des systèmes d’exploitation\n• Langage de commande : commandes de base, introduction à la programmation des scripts\n• Gestion des taches (création, destruction, suivi, etc.), des fichiers (types, droits, etc.) et des utilisateurs (caractéristiques, création, suppression, etc.)\n• Principes de l’installation et de la configuration d’un système.\n• Introduction aux outils bureautique', 1, 5, 10, 15, 10),
(2, 1, NULL, 'DUTINFO 1112', 'Introduction à l\'algorithmique et à la programmation', '• Comprendre la démarche méthodologique de la programmation informatique\n• Connaître un formalisme algorithmique\n• Savoir analyser un problème pour en tirer une solution formelle\n• Savoir écrire un algorithme d’une solution formelle\n• Connaître quelques algorithmes fondamentaux sur des données numériques et alphanumériques\n• Savoir analyser et comparer des algorithmes et rendre plus performant un algorithme\n• Connaître un langage de programmation informatique pour transposer les algorithmes fondamentaux', 'Aucun', '• Notion d\'information et de modélisation.(Analyse descendante, Analyse ascendante, Primitives et combinaisons de primitives du processeur algorithmique de référence)\n• Structures algorithmiques fondamentales (séquence, choix, itération, etc.)\n•  Présentation du Formalisme algorithmique\n• Notion de type\n•  Procédures et Fonctions algorithmiques\n•  Récursivité et dérécursification\n• Implantation en langage de programmation.', 4, 30, 10, 20, 30),
(3, 1, NULL, 'DUTINFO 1113', 'Technologie des ordinateurs', '• Connaître les méthodes de codage et de représentation de l’information, et les traitements associés.\n• Connaître le fonctionnement des circuits combinatoires associés au traitement des données $ Réaliser les cirscuits combinatoires.', 'Aucun', '• Codage de l’information : numération, représentation des nombres et codage en machines, codage des caractères, arithmétique et traitement associés.\n•  Éléments logiques : algèbre de Boole, circuits logiques combinatoires (décodeur, additionneur, unité de calcul), systèmes séquentiels simples (registres, compteurs).', 3, 8, 10, 12, 10),
(4, 1, NULL, 'DUTINFO 1114', 'Introduction aux Réseaux', '• Connaître les principes de la transmission et du codage de l\'information.\n• Connaître les principales techniques de transport mises en oeuvre dans les réseaux.', 'Aucun', '• Concepts fondamentaux des réseaux\n• Transmission de l\'information : support, topologie, codages, techniques d\'accès, partage.\n• Gestion des communications dans le réseau : synchronisation, contrôle d\'erreurs, contrôle de flux, routage, adressage, commutation\n• Technologie des réseaux locaux : Ethernet, FDDI, WiFi, etc.', 2, 14, 16, 0, 10),
(5, 1, NULL, 'DUTINFO 1115', 'Utilisation de Systèmes d\'exploitation', '• Connaître les bases théoriques et pratiques minimales des systèmes d’exploitation • Savoir utiliser un système d’exploitation multitâches, multi – utilisateurs.', 'Aucun', '• Types et caractéristiques des systèmes d’exploitation • Différents environnements des systèmes d’exploitation •  Interaction avec le système d’exploitation', 2, 12, 0, 38, 20),
(6, 2, NULL, 'DUTINFO 1121', 'Mathématiques discrétes', 'Mettre en œuvre des algorithmes de théorie des graphes ( Algo de + court chemin…).', 'Aucun', '•Vocabulaire de la théorie des ensembles, relations, ensembles ordonnés.\n• Éléments de théorie des graphes : graphes orientés et non orientés .Exemples d’algorithmes de plus courts chemins, de parcours et d’arbre couvrant de poids minimum.\n• Algèbre de Boole', 2, 10, 13, 7, 10),
(7, 2, NULL, 'DUTINFO 1122', 'Mathématiques pour l\'informatique', 'Mettre en œuvre des schémas de raisonnement (contraposée, absurde, récurrence, etc.).\n• Mettre en œuvre des algorithmes d\'arithmétique (Euclide, Bézout, etc.).\n• Faire le lien entre langage usuel et langage formalisé (propositions et prédicats).', 'Aucun', '•Logique : calcul propositionnel et calcul des prédicats.\n• Arithmétique : nombres premiers, division euclidienne, congruences.\n• Éléments de langages et d\'automates.', 2, 10, 13, 7, 10),
(8, 2, NULL, 'DUTINFO 1123', 'Algèbre linéaire et Géométrie', '• Savoir mettre en œuvre les méthodes de pivot.\n• Savoir représenter matriciellement des transformations géométriques.', 'Aucun', '• Espaces vectoriels de dimension finie et applications linéaires.\n• Systèmes d\'équations linéaires : aspects matriciels et numériques .\n• Calcul matriciel et diagonalisation\n• Transformations géométriques usuelles.', 3, 20, 20, 10, 30),
(9, 3, NULL, 'DUTINFO 1131', 'Environnement économique', '• Avoir une vision globale des problèmes économiques contemporains.', 'Aucun', '• Concepts de base et outils d’analyse économique : analyse du circuit économique\n• Questions économiques contemporaines : consommation, investissement, financement, emploi, redistribution, mondialisation, etc.', 2, 20, 10, 0, 10),
(10, 3, NULL, 'DUTINFO 1132', 'Introduction aux sciences juridiques', '• Être capable de comprendre les droits et obligations de l’informaticien dans l’exercice de sa profession.', 'Aucun', '• Approche générale du droit : introduction à l’étude du droit, notions générales de droit des contrats,  structures juridiques des entreprises,  droit du travail  et spécificités du contrat de travail de l’informaticien.\n• Droit des Technologies de l’Information et de la Communication (T.I.C.) : protection des données personnelles, sécurité des systèmes et des données, protection des créations intellectuelles,  aspects contractuels  des T.I.C., cyberdroit (les réseaux : Internet, Intranet, télécommunications).', 2, 20, 10, 0, 10),
(11, 3, NULL, 'DUTINFO 1133', 'Techniques de recherche documentaire', '• Intégrer les ressources documentaires dans le processus de pensée et d’action ;\n• Découvrir la chaîne des opérations documentaires ;\n• Connaître la typologie des documents ;\n• Etre apte à indexer un sujet (choisir les mots-clés pertinents) ;\n• Etre apte à prendre des notes ;\n• Maîtriser les techniques de recherche sur internet.', 'Aucun', '• Chaîne documentaire ;\n• Typologie des documents sur supports imprimé ;\n• Indexation du sujet ;\n• Structure du texte, du paragraphe et de la phrase dans la prose scientifique ;\n• Techniques de prise de notes :\n• Recherche sur internet.', 2, 8, 0, 12, 20),
(12, 3, NULL, 'DUTINFO 1134', 'Anglais  technique', '• Comprendre le vocabulaire anglais technique', 'Aucun', '• se présenter  et présenter autrui (maîtrise), •décrire, questionner et donner des réponses de base (maîtrise) • maîtriser l’anglais dans les situations de communication de la vie quotidienne\n (communication/maîtrise), • présenter les outils de communication dans le domaine de l\'Informatique et leurs usages (information/maîtrise).', 2, 12, 14, 4, 10),
(13, 4, NULL, 'DUTINFO 1211', 'Introduction aux SGBD', '• Concevoir et normaliser une base de donnée\n• Maîtriser le langage S.Q.L.', 'Aucun', '• Problématique de la gestion des données (SGF, ...)\n• S.G.D.B. : caractéristiques et fonctionnalités.\n• Algèbre relationnelle, langages prédicatifs.\n• Modèle de données relationnel.\n• Définition d’un schéma relationnel en S.Q.L., gestion des contraintes d’intégrité, notion de vue et d’index.\n• Interrogation et manipulation des données en S.Q.L. interactif.\n• Administration : gestion des utilisateurs et des privilèges, notions d’optimisation.', 4, 20, 20, 20, 40),
(14, 4, NULL, 'DUTINFO 1212', 'Langage C', '•Traduire un algorithme en C\n• Compiler, executer et debbuge un programme en C', 'Introduction à l\'algorithmique et à la programmation', '• VARIABLES, OPERATEURS ET EXPRESSIONS\n• LES SRUCTURES DE CONTROLES\n• Types derivés : LES TABLEAUX, Pointeurs, chaine de caracteres\n• LES FONCTIONS\n• LES STRUCTURES, unions, enumerations\n• LES FICHIERS', 3, 12, 12, 6, 10),
(15, 4, NULL, 'DUTINFO 1213', 'Algorithmique et structures de données', '• Connaître et savoir utiliser les principales structures de données. \n• Savoir concevoir des types de données. \n• Connaître et savoir utiliser les algorithmes fondamentaux.', 'Introduction à l\'algorithmique et à la programmation', '• Introduction à la structuration de données \n• Structures de données linéaires sous forme de tableaux (Vecteur et Matrice)\n• Algorithmes fondamentaux de traitement des tableaux (Recherche, Tris, fusion, etc…)\n• Fichiers de données et leurs traitements\n• Structures de données non linéaires (Listes, Piles, files, Arbre)\n• Algorithmes classiques de traitement de structures de données non linéaires\n• Projet de programmation d’intégration des connaissances.', 3, 20, 10, 20, 30),
(16, 4, NULL, 'DUTINFO 1214', 'Programmation web 1: HTML, JAVASCRIPT, CSS', '• Savoir développer une page Web statique.', 'Introduction à l\'algorithmique et à la programmation', '• HTML\n• CSS\n• Javascript', 3, 14, 0, 16, 10),
(17, 4, NULL, 'DUTINFO 1215', 'Architecture des ordinateurs', '• Manipuler les concepts du langage machine.\n• Connaître l’influence des architectures des microprocesseurs modernes sur les\n performances des programmes.', 'Technologie des ordinateurs', '• Microprocesseur : microprogrammation, séquencement, bus, langage machine, interruptions, composants externes (mémoire, contrôleurs, périphériques).\n•••••• Indications de mise en oeuvre :\n• Interactions souhaitables avec l’enseignement des mathématiques (représentation des nombres, algèbre de Boole).\n• L’étude du microprocesseur et de son environnement matériel peut faire l’objet de l’examen (voire de l’assemblage) d’un véritable ordinateur et de ses composants.', 2, 8, 10, 12, 10),
(18, 4, NULL, 'DUTINFO 1216', 'Architecture des Réseaux', '• Administrer des systèmes, des logiciels et de réseaux\n• Faire un Conseil et assistance technique à des utilisateurs, clients, services \n• Élaborer des diagnostics quantitatifs et qualitatifs, support technique du logiciel', 'Aucun', '•Étude d\'architectures de réseaux, incluant les modèles OSI (Open Systems Interconnection) et la pile TCP/IP (Transmission Control Protocol / Internet Protocol)\n • Technologie des réseaux locaux : Ethernet, WiFi (Wireless Fidelity), etc.\n • Routage, commutation, adressage, transport\n • Introduction à l’installation et la configuration d\'un réseau', 2, 8, 10, 12, 10),
(19, 4, NULL, 'DUTINFO 1217', 'Utilisation des SE et Scripts', 'Avoir une vision globale des Systèmes UNIX. Savoir Ecrire et Exécuter les scripts', 'Utilisation des Systémes d\'exploitation', '• Introduction aux Systèmes UNIX\n• Etude des variables systèmes \n• Etude des procédures : définition de scripts, exception de scripts ,\n• Les structures de contrôles : structures conditionnelles, structures itératives, • branchements, et opérateurs \n• Les commandes «test», «expr» et «find»', 2, 8, 6, 16, 10),
(20, 5, NULL, 'DUTINFO 1221', 'Analyse', '• Savoir majorer, minorer, encadrer. \n• Savoir calculer des limites, dériver, intégrer. \n• Savoir étudier localement une fonction. \n• Savoir gérer des approximations.', 'Limites, continuite, derivation', '• Suites et fonctions numériques\n•  Limites et convergence\n• Comportement local (dérivabilité, approximations) $ Fonctions réelles d\'une variable réelle (limites, continuité, dérivation, intégration).\n•  Approximation d\'une fonction numérique (théorèmes de Taylor).', 3, 20, 20, 0, 20),
(21, 5, NULL, 'DUTINFO 1222', 'Probabilité', '• Mesurer une incertitude sur une estimation ou une prévision.\n• Évaluer l\'adéquation d\'un modèle à une série observée', 'aucun', '• Notions de base de probabilités (conditionnement, indépendance, etc.). \n• Variables aléatoires discrètes et variables aléatoires continues. Lois discrètes (notion de séries)\n• Lois continues (éléments du calcul intégral)\n• Loi des grands nombres et théorème central limite', 2, 12, 18, 0, 10),
(22, 6, NULL, 'DUTINFO 1231', 'Economie (fonctionnement de l\') d\'entreprise', '• Appréhender l’entreprise en utilisant une perspective systémique.\n• Connaître les principaux domaines fonctionnels de l’entreprise et se familiariser aux méthodes de gestion.\n• Comprendre l’élaboration et la mise en œuvre d’une stratégie d’entreprise.', 'Aucun', '• Identité de l’entreprise : éléments constitutifs, rôle et place dans l’environnement.\n• Activités de l’entreprise.\n• Économie industrielle.\n• Démarche stratégique : objectifs et finalités, choix et actions stratégiques, stratégies, structures et systèmes d’information.\n• Organisation de l’entreprise : théorie des organisations, différentes structures  d’entreprise, pouvoir, décision, systèmes d’information.\n••••• Indication de mise en oeuvre :\n• S’appuyer sur l’étude du marché de l’informatique et des T.I.C.', 2, 18, 12, 0, 10),
(23, 6, NULL, 'DUTINFO 1232', 'Techniques de communication', '• Approfondir la maîtrise de la langue. \n• Améliorer la compétence à l’écoute et à la lecture (de textes, d’images, etc.).\n• Sensibiliser les étudiants à l’organisation de la pensée dans ses productions écrites  et orales. \n• Familiariser les étudiants avec des méthodes de travail intellectuel qui faciliteront leur  travail d’apprentissage.', 'Aucun', '• Approche des mécanismes d’appropriation des connaissances et des pratiques.\n• Pratique soutenue de lecture. \n• Analyse de l’image. Les idées : les trouver, les organiser, les argumenter, les présenter. \n• Productions écrites. \n• Prise de notes. \n• Mise en forme et lisibilité des documents informatisés (traitement de texte, tableau, logiciel de présentation, courriel). \n• Travail de groupe.', 2, 12, 18, 0, 10),
(24, 6, NULL, 'DUTINFO 1233', 'Anglais: Techniques d\'expression', '• Développer les aptitudes à la compréhension et à l\'expression écrite et orale en langue de communication.', 'Aucun', '• Langue générale :\n-Compréhension  écrite  :  lecture  rapide  ;  lecture  approfondie  de  textes  d\'intérêt général, etc.\n-Expression écrite : prise de notes, courriers, demande de renseignement, etc.\n-Compréhension auditive, expression orale : conversations courantes,  accueil d\'un visiteur étranger, déplacements, séjours en pays anglophones, etc.', 2, 12, 14, 4, 10),
(25, 7, NULL, 'DUTINFO 2311', 'Modélisation des Systèmes informatiques', '• Comprendre et maitriser les concepts du Génie Logiciel \n • Savoir identifier les processus et les cycles de vie   •Distinguer les différentes méthodes \n • Analyser un système d\'information\n  • Modéliser un système informatique avec UML', 'Aucun', '. Introduction au Génie Logiciel • Modélisation objet pour l’analyse et la conception détaillée par exemple en UML (Unified Modeling\n Language) :\n • Production de tests unitaires, problématique de la non régression\n • Gestion des versions dans le développement\n • Documentation du code\n • Sensibilisation aux bonnes pratiques de la conception et du développement', 4, 24, 24, 12, 30),
(26, 7, NULL, 'DUTINFO 2312', 'Programmation web 2: PHP/MYSQL', '• Savoir développer une application Web dynamique.', 'Programmation web 1, Introduction aux bases de données', '• Sructure d\'une application Web et concepts associés\n• Modèles d\'applications Web\n• Mise en oeuvre avec PHP et MySQL', 3, 14, 0, 16, 10),
(27, 7, NULL, 'DUTINFO 2313', 'Programmation par objets', '• Dé́velopper une application à l\'aide du concept objet.\n• Utiliser des bibliothèques ainsi que la documentation relative aux objets (API).', 'Algorithmique, langage C', '• Concepts de base d’un langage objet (classe, attribut, méthode, etc.). \n• Principe et utilisation des héritages (spécialisation, implémentation, etc.).\n• Polymorphisme. $Utilisation de bibliothèques de classes \n• Approfondissement des notions permettant la réutilisation (héritage, interface,\n paquetage, généricité, etc.). \n• Appllication dans un langage orienté-objet (Java)', 3, 10, 15, 15, 20),
(28, 7, NULL, 'DUTINFO 2314', 'Système d\'exploitation', '• Connaître les principaux principes et concepts des systèmes d’exploitation au niveau interne.\n• Connaître quelques mécanismes de mise en œuvre des systèmes d’exploitation multitâches, multi – utilisateurs.', 'Technologie des ordinateurs, Systeme d\'exploitation et scripts', '• Concepts généraux \n• Architectures de noyau.\n• Système d’entrée-sortie.\n• Partage de l’unité centrale.\n• Création, états, coopération de processus, exclusion mutuelle.\n• Mise en oeuvre des processus.\n• Outils et modèles de synchronisation - Interblocage.\n• Système de gestion de fichiers.\n• Gestion de la mémoire centrale.\n• Notions aux Signaux systèmes \n• Notions tubes de communication', 3, 20, 20, 20, 30),
(29, 7, NULL, 'DUTINFO 2315', 'Administration des Services Réseaux', '• Savoir choisir un réseau local ou public.\n• Construire un cahier des charges : Ingénierie des réseaux.', 'Systeme d\'exploitation et scripts', '• Architecture d’un réseau d’entreprise\n• Installation et configuration des services\n• Gestion des utilisateurs\n• Sécurité du système et du réseau : listes d’accès et de contrôle, authentification, etc.\n• Protocoles sécurisés, réseaux privés virtuels\n• Annuaires: LDAP (Lightweight Directory Access Protocol), AD (Active Directory), etc.\n• Chiffrement de données\n• Outils de supervision\n• Mise en oeuvre d’une stratégie de sauvegarde', 2, 14, 14, 22, 30),
(30, 8, NULL, 'DUTINFO 2321', 'Statistiques', '• Mesurer une incertitude sur une estimation ou une prévision.\n• Évaluer l\'adéquation d\'un modèle à une série observée .', 'Probabilité', '• Description uni et bi-variées de données statistiques. \n• Éléments de statistique inférentielle (estimation, tests dans les cas les plus simples). \n• Simulations. \n• Corrélation et régression simple.', 2, 18, 12, 0, 10),
(31, 8, NULL, 'DUTINFO 2322', 'Recherche opérationnelle', '• Mettre en œuvre des algorithmes de théorie des graphes à partir des modélisations', 'Aucun', '• Compléments de théorie des graphes.    • Cheminements optimaux dans un réseau, probléme d\'ordonnancement, probléme de flots et de circulation dans un réseau.', 2, 10, 10, 10, 10),
(32, 8, NULL, 'DUTINFO 2323', 'Gestion de Projet', 'Connaître l’organisation des ressources nécessaires à une gestion efficace des services informatiques de l’entreprise et à la réussite des projets dans les meilleures conditions (coûts, délais, qualité)', 'Aucun', '• T.I.C. et management de l’entreprise : organisation de la fonction informatique, entreprise étendue, entreprise intégrée\n • Contrôle de gestion informatique : connaissance, maîtrise et budgétisation des coûts. \n • Gestion de projets informatiques : structure, planification et suivi de projet', 2, 10, 20, 0, 10),
(33, 9, NULL, 'DUTINFO 2331', 'Gestion de l\'entreprise', '• Savoir lire et interpréter un bilan et un compte de résultat.\n• Savoir calculer des coûts pertinents dans des situations simples.\n• Comprendre l’importance d’un système de prévisions fiables et pertinentes.', 'Economie d\'entreprise', '• Principes  d’organisation  et  d’analyse  du   S.  I.   E.  F.  (Système  d’Information\nEconomique et Financier).\n• Approche   du   calcul   des   coûts   et   de   la   gestion   prévisionnelle   (vente, approvisionnement, production, etc.).', 2, 10, 20, 0, 10),
(34, 9, NULL, 'DUTINFO 2332', 'Environnement socio-culturel de l\'entreprise', '• Permettre une meilleure compréhension de la complexité et de la diversité de la culture et de la société. • Appréhender les enjeux humains et sociaux liés au développement des technologies de l’information et de la communication (T.I.C.).', 'Environnement économique', '• Interrogation  sur  la  notion  de  culture  :  contemporaine,  écrite,  visuelle ;  pluralité culturelle.\n• Culture et actualité.\n• Cultures et civilisations (interactions (d’un point de vue social, religieux, etc.), mise en perspective historique).\n• Production culturelle : origines, élaborations, diffusions et réceptions.\n• Réflexion sur l’informatique dans la société.\n• Enjeux sociaux et humains liés au développement des  TIC', 2, 12, 18, 0, 10),
(35, 9, NULL, 'DUTINFO 2333', 'Anglais des affaires', '• Développer les aptitudes à la compréhension et à  l\'expression écrite et orale dans un cadre professionnel.\n• Développer ces aptitudes en préparation à des périodes d\'études ou de stages dans des pays anglophones.', 'Anglais: Techniques d\'expression', '• Langue de la communication professionnelle :\n-Compréhension écrite : offres d\'emploi, présentation d\'entreprises, etc.\n-Expression écrite : notes et rapports, curriculum vitae, lettres de motivation, courrier professionnel, courriel, etc.', 2, 16, 14, 0, 10),
